import React from 'react';
import { View, Text, ImageBackground } from 'react-native';
import Navbar from '../Navbar';
import Footer from '../Footer';
import Snowfall from 'react-snowfall';

const Challenges = ({ navigation }) => {
    const challenges = [
        {
            id: 1,
            title: 'Drink x amount of water',
            description: 'Drink 8 glasses of water every day to stay hydrated.',
        },
        {
            id: 2,
            title: 'Turn off lights when not in use',
            description: 'Make sure to turn off lights when you leave a room to save energy.',
        },
        // Add more challenges here
    ];

    const styles = {
        container: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
        },
    };

    return (
        <View style={{ flex: 1 }}>
            <Navbar />
            <ImageBackground source={require("../../../assets/ecoBackground.png")} style={{ flex: 1, ...styles.container, overflow: 'hidden' }}>
                <Text style={{ fontSize: 24, fontWeight: 'bold' }}>Daily Challenges</Text>
                {challenges.map(challenge => (
                    <View key={challenge.id}>
                        <Text style={{ fontSize: 18, fontWeight: 'bold' }}>{challenge.title}</Text>
                        <Text>{challenge.description}</Text>
                    </View>
                ))}
                <Image source={require("../../../assets/ecoTreesSnow.png")} style={{ position: 'absolute', bottom: -40, width: '100%', height: 160 }} />
            </ImageBackground>
            <Snowfall snowflakeCount={250} />
            <Footer style={{ height: 18 }} navigation={navigation} />
        </View>
    );
};

export default Challenges;
