import React, { useState } from 'react';
import { View, Text, Image, ImageBackground, FlatList, TouchableOpacity } from 'react-native';
import { MaterialIcons } from '@expo/vector-icons'; // install @expo/vector-icons
import Navbar from '../Navbar';
import Footer from '../Footer';
import Snowfall from 'react-snowfall';

const Challenges = ({ navigation }) => {
    const challenges = [
        {
            id: '1',
            title: 'Drink x amount of water',
            description: 'Drink 8 glasses of water every day to stay hydrated.',
        },
        {
            id: '2',
            title: 'Turn off lights when not in use',
            description: 'Make sure to turn off lights when you leave a room to save energy.',
        },
        // Add more challenges here
    ];

    const styles = {
        container: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
        },
        item: {
            padding: 20,
            marginVertical: 8,
            marginHorizontal: 16,
        },
        title: {
            fontSize: 32,
        },
    };

    const renderItem = ({ item }) => {
        const [isCompleted, setIsCompleted] = useState(false);

        return (
            <View style={styles.item}>
                <TouchableOpacity onPress={() => setIsCompleted(!isCompleted)}>
                    <MaterialIcons name="check-circle" size={24} color={isCompleted ? 'green' : 'grey'} />
                </TouchableOpacity>
                <Text style={styles.title}>{item.title}</Text>
                <Text>{item.description}</Text>
            </View>
        );
    };


};

export default Challenges;
