import { StyleSheet } from 'react-native';

export default StyleSheet.create({
    factBox: {
        position: 'absolute',
        right: 10,
        bottom: 10,
        width: '50%',
        height: '30%',
        backgroundColor: 'white',
        padding: 10,
        borderRadius: 4,
        borderColor: 'rgba(0, 0, 0, 0.1)',
        justifyContent: 'flex-end',
    },
    closeButton: {
        backgroundColor: '#2196F3',
        padding: 10,
        marginTop: 10,
        width: 70,
        height: 30,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 4,
    },
});
