import React, { useState, useEffect } from 'react';
import { View, ScrollView, Text, FlatList, StyleSheet, ImageBackground, TouchableOpacity, Image } from 'react-native';
import Navbar from '../Navbar';
import Footer from '../Footer';
import Snowfall from 'react-snowfall';

const Challenges = () => {
    const challenges = [
        {
            id: 1,
            title: 'Drink x amount of water',
            description: 'Drink 8 glasses of water every day to stay hydrated.',
        },
        {
            id: 2,
            title: 'Turn off lights when not in use',
            description: 'Make sure to turn off lights when you leave a room to save energy.',
        },
        // Add more challenges here
    ];

    return (
        <View>
            <Text style={{ fontSize: 24, fontWeight: 'bold' }}>Daily Challenges</Text>
            {challenges.map(challenge => (
                <View key={challenge.id}>
                    <Text style={{ fontSize: 18, fontWeight: 'bold' }}>{challenge.title}</Text>
                    <Text>{challenge.description}</Text>
                </View>
            ))}
        </View>
    );
};

export default Challenges;
