import React, { useState } from 'react';
import { View, Text, Linking, TouchableOpacity, ImageBackground, TextInput } from 'react-native';
import Navbar from '../Navbar';
import Footer from '../Footer';

const Contact = () => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [inquiry, setInquiry] = useState('');

    const handleEmailPress = () => {
        const mailto = `mailto:EcoTracker2023@gmail.com?subject=Inquiry from ${firstName} ${lastName}&body=${inquiry}`;
        Linking.openURL(mailto);
    };

    return (
        <View style={{ flex: 1 }}>
            <Navbar />
            <ImageBackground
                source={require("../../../assets/ecoBackground.png")}
                style={{
                    flex: 1,
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
            >
                <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
                    <Text style={{ fontSize: 40, fontWeight: 'bold', marginBottom: 10 }}>
                        Contact Us
                    </Text>
                    <TextInput
                        style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                        onChangeText={setFirstName}
                        value={firstName}
                        placeholder="Your first name"
                    />
                    <TextInput
                        style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                        onChangeText={setLastName}
                        value={lastName}
                        placeholder="Your last name"
                    />
                    <TextInput
                        style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                        onChangeText={setEmail}
                        value={email}
                        placeholder="Your email address"
                    />
                    <TextInput
                        style={{ height: 40, borderColor: 'gray', borderWidth: 1 }}
                        onChangeText={setInquiry}
                        value={inquiry}
                        placeholder="Tell us your inquiry"
                    />
                    <TouchableOpacity onPress={handleEmailPress}>
                        <Text style={{ color: 'white', fontSize: 20 }}>Submit</Text>
                    </TouchableOpacity>
                </View>
            </ImageBackground>
            <Footer />
        </View>
    );
};

export default Contact;
