// NavbarStyles.js
import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    navbar: {
        backgroundColor: '#062a52',
        position: 'absolute', // Set to absolute positioning
        top: 0, // Position at the top
        left: 0, // Position at the left
        width: '100%', // Take the full width of the screen
        height: 80, // Adjust the height as needed for a thicker navbar
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: 0,
        zIndex: 1,
    },
    menuIcon: {
        fontSize: 30,
        color: 'white',
    },
    menuContainer: {
        backgroundColor: '#062a52',
        position: 'absolute',
        top: 80,
        right: 0,
        width: 200,
        padding: 10,
        zIndex: 2,
    },
    menuItem: {
        color: 'white',
        fontSize: 16,
        marginBottom: 10,
    },
});

export default styles;
