import React, { useState, useEffect } from 'react';
import { View, Text, TouchableOpacity, Image, ImageBackground } from 'react-native';
import Navbar from '../Navbar';
import Footer from '../Footer';

const Guide = () => {
    return (
        <View style={{ flex: 1 }}>

            <ImageBackground source={require("../../../assets/ecoBackground.png")} style={{ ...styles.container, overflow: 'hidden' }}>
                <Text style={{ fontSize: 20, marginBottom: 20 }}>Click here to learn the right way to recycle</Text>
                <TouchableOpacity onPress={() => Linking.openURL('https://www.wm.com/us/en/recycle-right/recycling-101')}>
                    <Image source={require("../../../assets/waterBottleEmpty.png")} style={{ width: 100, height: 100 }} />
                </TouchableOpacity>
            </ImageBackground>

        </View>
    );
};

export default Guide;
