import { StyleSheet, Dimensions } from "react-native";

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center'
    },
    image: {
        width: windowWidth * 0.2, // 20% of screen width
        aspectRatio: 1,
        resizeMode: "contain",
        marginTop: -100,
    },
    input: {
        width: windowWidth * 0.15, // 10% of screen width
        height: windowHeight * 0.05, // 5% of screen height
        fontSize: 18,
        padding: 10,
        margin: 10,
        marginTop: -100,
        borderColor: '#062A52',
        borderWidth: 2,
        borderRadius: 15,
        backgroundColor: 'white',
    },
    button: {
        width: windowWidth * 0.08, // 10% of screen width
        height: windowHeight * 0.05, // 5% of screen height
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#062A52',
        borderRadius: 15,
        marginTop: 10,
    },
    buttonText: {
        color: "white",
        fontSize: windowWidth * 0.01, // 1% of screen width
        fontWeight: "bold",
        textShadowOffset: { width: 1, height: 1 },
        textShadowRadius: 1,
        marginBottom: 3,
    },
});

export default styles;
