import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Image, ImageBackground, Animated, Easing, Button, TextInput, Alert, Dimensions, TouchableOpacity } from "react-native";
import styles from "../Styles/VerificationStyles";
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import Footer from '../Footer';
import axios from 'axios';




function PasswordResetTemp() {
    const [scaleValue] = useState(new Animated.Value(1));

    useEffect(() => {
        const pulseAnimation = Animated.sequence([
            Animated.timing(scaleValue, {
                toValue: 1.07,
                duration: 1500,
                easing: Easing.ease,
                useNativeDriver: true,
            }),
            Animated.timing(scaleValue, {
                toValue: 1,
                duration: 1500,
                easing: Easing.ease,
                useNativeDriver: true,
            }),
        ]);

        Animated.loop(pulseAnimation).start();
    }, [scaleValue]);



    const handleLogin = () => {
        navigation.navigate('Title');
    };
    //handleLogin();



    return (
        <ImageBackground
            source={require("../../../assets/ecoBackground.png")}
            style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100vh', // full viewport height
            }}
        >
            <Image source={require("../../../assets/ecoSun.png")}
                style={{
                    width: 300,
                    height: 300,
                    position: 'absolute',
                    bottom: 0
                }} />
            <Animated.Image
                source={require("../../../assets/ecoTrackLogosu.png")}
                style={[styles.logo, { transform: [{ scale: scaleValue }] }]}
            />


            <View style={{ ...styles.container, marginBottom: 100 }}>
                <Text style={{
                    ...styles.text, color: 'white', fontSize: 18, textShadowColor: 'black',
                    textShadowOffset: { width: 5, height: 5 },
                    textShadowRadius: 1,
                }}>Password Reset</Text>
                <Text style={{
                    ...styles.text, color: 'white', fontSize: 16, textShadowColor: 'black', textShadowOffset: { width: 1, height: 1 }, textShadowRadius: 1,
                }}>We've just sent the PASSWORD RESET to your Email</Text>


                <TouchableOpacity
                    style={styles.button}
                    onPress={async () => {
                        await handleLogin();

                    }}
                >
                    <Text style={styles.buttonText}>Go Home</Text>
                </TouchableOpacity>

            </View>



            <Footer style={{ height: 18 }} navigation={navigation} />
        </ImageBackground >

    );
}
export default PasswordResetTemp;
