import React, { useState, useEffect } from "react";
import { StyleSheet, Text, View, Image, Animated, Easing, Button, TextInput, Alert, TouchableOpacity } from "react-native";
import LinearGradient from 'react-native-linear-gradient';
import styles from "../Styles/LogInStyles";
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import axios from 'axios';




function LogIn() {
    const [scaleValue] = useState(new Animated.Value(1));

    useEffect(() => {
        const pulseAnimation = Animated.sequence([
            Animated.timing(scaleValue, {
                toValue: 1.15,
                duration: 1500,
                easing: Easing.ease,
                useNativeDriver: true,
            }),
            Animated.timing(scaleValue, {
                toValue: 1,
                duration: 1500,
                easing: Easing.ease,
                useNativeDriver: true,
            }),
        ]);

        Animated.loop(pulseAnimation).start();
    }, [scaleValue]);

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    //console.log(username, password)

    const handleLogin = async () => {
        try {
            // Make POST request using axios with data
            const response = await axios({
                method: 'POST',
                url: 'http://localhost:5000/login',
                headers: { 'Content-Type': 'application/json' },
                data: {
                    username: username,
                    password: password,
                }
            });

            //console.log(response.data);  // Print out the response data
        } catch (error) {
            console.error(error);
        }
    };

    //handleLogin();



    return (


        <View style={{ flex: 1 }}>
            <LinearGradient
                colors={['#439946', 'rgba(67, 153, 70, 0.21)']}
                style={{ flex: 1 }}
            >
                <Animated.Image
                    source={require("../../../assets/ecoTrackLogosu.png")}
                    style={[styles.logo, { transform: [{ scale: scaleValue }] }]}
                />
                <View style={styles.container}>
                    <TextInput
                        style={styles.input}
                        placeholder="Username or Email"
                        value={username}
                        onChangeText={text => setUsername(text)}
                        autoCapitalize="none"
                    />
                    <br></br>
                    <TextInput
                        style={styles.input}
                        placeholder="Password"
                        value={password}
                        onChangeText={text => setPassword(text)}
                        secureTextEntry={true}
                        autoCapitalize="none"
                    />
                    <br></br>
                    <TouchableOpacity
                        style={styles.button}
                        onPress={async () => {
                            await handleLogin();
                            navigation.navigate('Title');
                        }}
                    >
                        <Text style={styles.buttonText}>Sign In</Text>
                    </TouchableOpacity>
                </View>
                <Text style={styles.text}>Don't Have an Account? </Text>
                <TouchableOpacity onPress={() => navigation.navigate('SignUp')}>
                    <Text style={styles.linkText}>Sign Up</Text>
                </TouchableOpacity>
            </LinearGradient>
        </View >
    );
}
export default LogIn;
