import { StyleSheet, useWindowDimensions } from "react-native";

export default function useResponsiveStyles() {
    const windowWidth = useWindowDimensions().width;
    const windowHeight = useWindowDimensions().height;

    const styles = StyleSheet.create({
        container: {
            flex: 1,
            alignItems: 'center',
            justifyContent: 'center',
            padding: 20,
        },
        title: {
            fontSize: 24,
            fontWeight: 'bold',
            marginBottom: 20,
        },
        info: {
            fontSize: 18,
            marginBottom: 10,
        },

        button: {
            marginTop: 20,
        },
        buttonContainer: {
            width: '60%', // or any other value you want
            alignSelf: 'center',
        },
        infoContainer: {
            flexDirection: 'row',
            alignItems: 'center',
            justifyContent: 'space-between',
            marginBottom: 10,
        },
        icon: {
            fontSize: 18,
            marginLeft: 10,
        },
    });
    return styles;
}
