import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, ImageBackground, TouchableOpacity, Image } from 'react-native';
import axios from 'axios';
import Navbar from '../Navbar';
import Footer from '../Footer';
import Snowfall from 'react-snowfall';
import Modal from 'react-modal';

Modal.setAppElement('#root');

function News() {
    const [news, setNews] = useState([]);
    const [modalIsOpen, setIsOpen] = useState(false);
    const [modalUrl, setModalUrl] = useState('');

    useEffect(() => {
        const fetchNews = async () => {
            try {
                const response = await axios.get('https://newsapi.org/v2/top-headlines', {
                    params: {
                        q: 'environment',
                        apiKey: 'bde5b060f669431985e03cadc068dd43'
                    }
                });
                console.log(response); // Log the response here
                setNews(response.data.articles);
            } catch (error) {
                console.error(error);
            }
        };

        fetchNews();
    }, []);

    const openModal = (url) => {
        setModalUrl(url);
        setIsOpen(true);
    };


    const closeModal = () => {
        setIsOpen(false);
    };

    const renderItem = ({ item }) => {
        return (
            <TouchableOpacity style={styles.newsItem} onPress={() => openModal(item.url)}>
                <Image style={styles.image} source={{ uri: item.urlToImage }} />
                <Text style={styles.title}>{item.title}</Text>
            </TouchableOpacity>
        );
    };

    return (
        <View style={{ flex: 1, justifyContent: 'space-between' }}>
            <Navbar />
            <ImageBackground source={require("../../../assets/ecoBackgroundChristmas.png")} style={{ flex: 1, ...styles.container, overflow: 'hidden' }}>
                {news.length > 0 ? (
                    <FlatList
                        data={news}
                        renderItem={renderItem}
                        keyExtractor={item => item.url}
                    />
                ) : (
                    <View style={styles.placeholderContainer}>
                        <Text style={styles.placeholderText}>No news available</Text>
                    </View>
                )}
                <Modal
                    isOpen={modalIsOpen}
                    onRequestClose={closeModal}
                    contentLabel="Article Modal"
                    style={{
                        overlay: { display: 'flex', alignItems: 'center', justifyContent: 'center' },
                        content: { position: 'relative', width: '70%', height: '70%', overflow: 'hidden' } // Set overflow to 'hidden'
                    }}
                >
                    <iframe src={modalUrl} width="100%" height="100%"></iframe>
                    <button onClick={closeModal} style={{ position: 'absolute', top: 0, right: 0 }}>X</button>
                </Modal>
            </ImageBackground>
            <Snowfall style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0 }} snowflakeCount={250} />
            <Footer style={{ height: 18 }} navigation={navigation} />
        </View>
    );
}

const styles = StyleSheet.create({
    newsItem: {
        margin: 10,
        padding: 10,
        backgroundColor: '#fff',
        borderRadius: 10,
        width: '30%',
        alignSelf: 'center',
    },
    image: {
        width: '100%', // Set the width to 100% of the parent container
        height: 200, // Set a specific height
    },
    title: {
        fontSize: 18,
        fontWeight: 'bold',
    },
    source: {
        fontSize: 16,
    },
    url: {
        fontSize: 14,
        color: 'blue',
    },
});

export default News;
