import { StyleSheet, Dimensions } from "react-native";

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const styles = StyleSheet.create({
    image: {
        width: windowWidth * 0.1, // 20% of screen width
        aspectRatio: 1,
        resizeMode: "contain",
        marginBottom: -40,
    },
    input: {
        width: windowWidth * 0.1, // 80% of screen width
        height: windowHeight * 0.05, // 5% of screen height
        fontSize: 18,
        padding: 10,
        margin: 20,
        borderColor: '#062A52',
        borderWidth: 2,
        borderRadius: 5,
        backgroundColor: 'white',
        borderRadius: 15,
    },
    button: {
        width: windowWidth * 0.1, // 30% of screen width
        height: windowHeight * 0.05, // 5% of screen height
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#062A52',
        borderRadius: 5,
        marginTop: 10,
        borderRadius: 15,
    },
    buttonText: {
        color: "white",
        fontSize: windowWidth * 0.01, // 5% of screen width
        fontWeight: "bold",
        textShadowOffset: { width: 1, height: 1 },
        textShadowRadius: 1,
        marginBottom: 3,
    },
});

export default styles;
