import { StyleSheet } from "react-native";

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "flex-start",
        backgroundColor: "transparent",
        marginTop: -50,
    },
    title: {
        width: "25%", // Adjusted the percentage to make the size responsive (bc chad/rebecca said responsive is cool)
        aspectRatio: 1, // Maintain the aspect ratio for responsive scaling (bc chad/rebecca said responsive is cool)
        resizeMode: "contain",
    },
    input: {
        width: 400,
        height: 50,
        fontSize: 18,
        margin: 10,
        borderColor: '#062A52',
        borderWidth: 2,
        borderRadius: 5,
        backgroundColor: 'white',
        borderRadius: 15,
    },
    button: {
        width: 120,
        height: 40,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#062A52',
        borderRadius: 5,
        marginTop: 10,
        borderRadius: 15,
    },
    buttonText: {
        color: "white",
        fontSize: 20,
        fontWeight: "bold",
        textShadowOffset: { width: 1, height: 1 },
        textShadowRadius: 1,
    },
    errorText: {
        color: "red",
        fontSize: 16,
        marginTop: 10,
    },
});

export default styles;
