import React, { useState, useEffect } from 'react';
import { View, Text, Image, TouchableOpacity, Linking, ImageBackground } from 'react-native';
import Navbar from '../Navbar';
import Footer from '../Footer';

const Guide = () => {
    return (
        <View style={{ flex: 1 }}>
            <Navbar />
            <ImageBackground
                source={require("../../../assets/ecoBackground.png")}
                style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}
                resizeMode='cover'>
                <Text style={{ fontSize: 20, marginBottom: 20 }}>Click Below to Learn the Right Way to Recycle</Text>
                <TouchableOpacity
                    onPress={() => Linking.openURL('https://www.wm.com/us/en/recycle-right/recycling-101')}
                    style={{ width: '100%', height: '30%' }}>  {/* Reduce this percentage to shrink the image */}
                    <Image source={require("../../../assets/Recycle.png")} style={{ width: '100%', height: '100%' }} />
                </TouchableOpacity>
            </ImageBackground>
            <Footer style={{ height: 18 }} navigation={navigation} />
        </View>
    );
};

export default Guide;
