import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
    navbar: {
        backgroundColor: 'blue',
        height: 80, // Adjust the height as needed for a thicker navbar
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: 10,
    },
    menuIcon: {
        fontSize: 30,
        color: 'white',
    },
    menuContainer: {
        display: 'none', // Initially hidden
        backgroundColor: 'blue',
        position: 'absolute',
        top: 80, // Adjust the top position to align with the navbar
        right: 0,
        width: 200, // Adjust the width as needed
        padding: 10,
    },
    menuItem: {
        color: 'white',
        fontSize: 16,
        marginBottom: 10,
    },
});

export default styles;
