import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, Image, ImageBackground } from 'react-native';
import useResponsiveStyles from '../Styles/TrackStyles';
import Navbar from '../Navbar';
import Footer from '../Footer';
import Snowfall from 'react-snowfall';

const Water = () => {
    const [waterIntake, setWaterIntake] = useState(0);
    const recommendedIntake = 10 - 15; // The recommended average water intake in cups

    return (
        <View style={{ flex: 1 }}>
            <Navbar />
            <ImageBackground source={require("../../../assets/ecoBackground.png")} style={{ ...styles.container, overflow: 'hidden' }}>
                <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
                    <Text style={{ fontSize: 24 }}>Recommended water intake: {recommendedIntake} cups per day</Text>
                    <View style={{ flexDirection: 'row', alignItems: 'center', marginTop: 20 }}>
                        <TouchableOpacity onPress={() => setWaterIntake(Math.max(0, waterIntake - 1))}>
                            <Text style={{ fontSize: 50 }}>-</Text>
                        </TouchableOpacity>
                        {/* <Image source={require('./path_to_your_image.png')} style={{ width: 100, height: 100 }} /> */}
                        <TouchableOpacity onPress={() => setWaterIntake(waterIntake + 1)}>
                            <Text style={{ fontSize: 50 }}>+</Text>
                        </TouchableOpacity>
                    </View>
                    <Text style={{ fontSize: 24, marginTop: 20 }}>Your water intake: {waterIntake} cups</Text>
                </View>

            </ImageBackground>
            <Snowfall snowflakeCount={250} />
            <Footer style={{ height: 18 }} navigation={navigation} />
        </View>
    );
};

export default Water;
