import React, { useState, useEffect } from 'react';
import { View, Text, Image, FlatList, StyleSheet, ImageBackground } from 'react-native';
import axios from 'axios';
import Navbar from '../Navbar';
import Footer from '../Footer';
import Snowfall from 'react-snowfall';
import useResponsiveStyles from '../Styles/TrackStyles';

function News() {
    const [news, setNews] = useState([]);

    useEffect(() => {
        const options = {
            method: 'GET',
            url: 'https://environment-news-live.p.rapidapi.com/news',
            headers: {
                'X-RapidAPI-Key': '4230925196msh9f168ff6748afdbp19c2fdjsn57cb79111842',
                'X-RapidAPI-Host': 'environment-news-live.p.rapidapi.com'
            }
        };

        const fetchNews = async () => {
            try {
                const response = await axios.request(options);
                setNews(response.data); // adjust this line based on the structure of the API response
            } catch (error) {
                console.error(error);
            }
        };

        fetchNews();
    }, []);

    const renderItem = ({ item }) => (
        <View style={styles.newsItem}>
            <Text style={styles.title}>{item.title}</Text>
            <Text style={styles.source}>{item.source}</Text>
            <Text style={styles.url}>{item.url}</Text>
        </View>
    );

    return (
        <View style={{ flex: 1, justifyContent: 'space-between' }}>
            <Navbar />
            <ImageBackground source={require("../../../assets/ecoBackgroundChristmas.png")} style={{ flex: 1, ...styles.container, overflow: 'hidden' }}>
                {news.length > 0 ? (
                    <FlatList
                        data={news}
                        renderItem={renderItem}
                        keyExtractor={item => item.url}
                    />
                ) : (
                    <View style={styles.placeholderContainer}>
                        <Text style={styles.placeholderText}>No news available</Text>
                    </View>
                )}
            </ImageBackground>
            <Snowfall style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0 }} snowflakeCount={250} />
            <Footer style={{ height: 18 }} navigation={navigation} />
        </View>
    );
}

const styles = StyleSheet.create({
    newsItem: {
        margin: 10,
        padding: 10,
        backgroundColor: '#fff',
        borderRadius: 10,
    },
    title: {
        fontSize: 18,
        fontWeight: 'bold',
    },
    image: {
        width: '100%',
        height: 200,
        resizeMode: 'cover',
        marginVertical: 10,
    },
    description: {
        fontSize: 16,
    },
});
export default News;
