import { StyleSheet, Dimensions } from "react-native";

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "flex-start",
        backgroundColor: "transparent",
        marginTop: windowHeight * -0.01, // 1% of screen height
    },
    title: {
        width: "15%", // Reduced from 20%
        aspectRatio: 1,
        resizeMode: "contain",
        marginBottom: windowHeight * -0.01, // 0.5% of screen height
    },
    input: {
        width: windowWidth * 0.2, // Reduced from 0.3 (30% of screen width)
        height: windowHeight * 0.02, // Keep the same (2% of screen height)
        fontSize: windowWidth * 0.02, // Reduced from 0.03 (3% of screen width)
        padding: windowWidth * 0.01, // Reduced from 0.02 (2% of screen width)
        margin: windowWidth * 0.02, // Reduced from 0.04 (4% of screen width)
        borderColor: '#062A52',
        borderWidth: 2,
        borderRadius: 15,
        backgroundColor: 'white',
    },
    button: {
        width: windowWidth * 0.05, // 30% of screen width
        height: windowHeight * 0.05, // 5% of screen height
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#062A52',
        borderRadius: 15,
        marginTop: windowHeight * 0.01, // 1% of screen height
    },
    buttonText: {
        color: "white",
        fontSize: windowWidth * 0.01, // 5% of screen width
        fontWeight: "bold",
        textShadowOffset: { width: 1, height: 1 },
        textShadowRadius: 1,
        marginBottom: 3,
    },
});

export default styles;
