import { StyleSheet } from 'react-native';

const NAVBAR_HEIGHT = 80;
const MENU_WIDTH = 200;
const FONT_SIZE = 16;
const COLOR_WHITE = 'white';
const COLOR_NAVBAR = '#062a52';

const styles = StyleSheet.create({
    navbar: {
        backgroundColor: COLOR_NAVBAR,
        position: 'absolute',
        top: 0,
        left: 0,
        width: '100%',
        height: NAVBAR_HEIGHT,
        flexDirection: 'row',
        alignItems: 'center',
        justifyContent: 'space-between',
        padding: 10,
        zIndex: 1,
    },
    menuIcon: {
        fontSize: FONT_SIZE * 2, // Make the menu icon larger
        color: COLOR_WHITE,
    },
    menuContainer: {
        backgroundColor: COLOR_NAVBAR,
        position: 'absolute',
        top: NAVBAR_HEIGHT,
        right: 0,
        width: MENU_WIDTH,
        padding: 10,
        zIndex: 2,
    },
    menuItem: {
        color: COLOR_WHITE,
        fontSize: FONT_SIZE,
        marginBottom: FONT_SIZE, // Space the menu items out a bit more
    },
});

export default styles;
